#!/bin/bash#SBATCH --job-name=dynamic_array#SBATCH --time=2:59:00#SBATCH --cpus-per-task=2#SBATCH --mem=4000M#SBATCH -D ./# Environment setupmodule load slurm_setupsource "$HOME/miniconda3/etc/profile.d/conda.sh"conda activate steinstadion-envexport TMPDIR=/tmpexport MPLCONFIGDIR=/tmp/matplotlibmkdir -p /tmp/matplotlibexport PYTHONPATH=$PYTHONPATH:$HOME/steinstadion# Debug: print environment variablesecho "HOME = $HOME"echo "USER = $USER"echo "TMPDIR = $TMPDIR"echo "PYTHONPATH = $PYTHONPATH"# Read the command for this array taskCMD=$(sed -n "${SLURM_ARRAY_TASK_ID}p" "$1")echo "Running command: $CMD"eval "$CMD"